import React, { useState } from 'react';
import { Button, Col, Container, Row } from 'react-bootstrap';
import { ACCEPTED_REQUESTS_ROUTE, APPROVED_REQUESTS_ROUTE, REJECTED_REQUESTS_ROUTE, STATISTICS_ROUTE } from '../utils/consts';
import Navbar from 'react-bootstrap/Navbar';
import { NavLink, useLocation } from 'react-router-dom';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';

const [radioValue, setRadioValue] = useState('1');

const radios = [
    { name: 'Принятые заявки', value: '1' },
    { name: 'Одобренные заявки', value: '2' },
    { name: 'Отклоненные заявки', value: '3' },
  ];

const Requests = () => {
    const location = useLocation()
    const isAccepted = location.pathname === ACCEPTED_REQUESTS_ROUTE
    const isApproved = location.pathname === APPROVED_REQUESTS_ROUTE
    const isRejected = location.pathname === REJECTED_REQUESTS_ROUTE
    return (
        <Container>
            <Navbar bg="light" variant="light">
                <NavLink to={STATISTICS_ROUTE}>Статистика</NavLink>
            </Navbar>
            <ButtonGroup>
                {radios.map((radio, idx) => (
                    <ToggleButton
                        key={idx}
                        id={`radio-${idx}`}
                        type="radio"
                        variant={idx % 2 ? 'outline-success' : 'outline-danger'}
                        name="radio"
                        value={radio.value}
                        checked={radioValue === radio.value}
                        onChange={(e) => setRadioValue(e.currentTarget.value)}
                            >
                        {radio.name}
                    </ToggleButton>
                ))}
            </ButtonGroup>
        </Container>
    );
};

export default Requests;
