import React from 'react';
import { Col, Container } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

const PetCard = ({pet}) => {
    let age = pet.age
    return (
        <Col md={3}>
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" height={250} src={pet.photo} />
                <Card.Body>
                    <Card.Title>{pet.name}</Card.Title>
                    <Card.Text>
                        <p>{pet.breed}</p>
                        <p>Пол: {pet.gender}</p>
                        {(age === 1 && <p>Возраст: age год</p>) || 
                        (age === 2 && <p>Возраст: age года</p>) ||
                        (age === 3 && <p>Возраст: age года</p>) ||
                        (age === 4 && <p>Возраст: age года</p>) ||
                        <p>Возраст: {age} лет</p>}
                    </Card.Text>
                    <Button variant="primary">Забрать</Button>
                </Card.Body>
            </Card>            
        </Col>
    );
};

export default PetCard;
