import React, { useContext } from 'react';
import {Context} from "../index";
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { NavLink, useLocation } from 'react-router-dom';
import { HOME_ROUTE, PERSONAL_CABINET_ROUTE, REGISTRATION_ROUTE } from '../utils/consts';
import {Button} from 'react-bootstrap';
import {observer} from "mobx-react-lite";

const NavBar = observer(() => {
    const {user} = useContext(Context)
    const location = useLocation()
    const isLogin = location.pathname === LOGIN_ROUTE
    const isReg = location.pathname === REGISTRATION_ROUTE
    return (
        <Navbar bg="dark" variant="dark">
          <NavLink to={HOME_ROUTE}>Хвостики</NavLink>
            {user.isAdmin && !isLogin && !isReg ?
                <Nav className="ml-auto" style={{color: 'white'}}>
                    <h6>Вы вошли как администратор</h6>
                </Nav>
                :
                <div></div>
            }
            {!user.isAdmin && !isLogin && !isReg ?
                <Nav className="ml-auto">
                    <Button>Личный кабинет</Button>
                </Nav>
                :
                <div></div>
            }
            
      </Navbar>
    );
});

export default NavBar;
